insert into BOOK values (310, "Thần Đồng Đất Việt 42 - Ngôi Làng Xấu Tính", 10000.0, 9100.0, false, false, false, false, true, current_timestamp(), 88, "Thần Đồng Đất Việt 42 - Ngôi Làng Xấu Tính Vụ án mất bò, hai làng ấu đả! Ba lần phân xử, bò về khổ chủ, tâm phục khẩu phục! Cột mốc ngụy tạo, vạch mặt kẻ gian! Đó là hai vụ xử án tiêu biểu của Tiến sĩ Phạm Công Trứ và Cử nhân Vũ Văn Tuấn.;", "Thần Đồng Đất Việt 42 - Ngôi Làng Xấu Tính Vụ án mất bò, hai làng ấu đả! Ba lần phân xử, bò về khổ chủ, tâm phục khẩu phục! Cột mốc ngụy tạo, vạch mặt kẻ gian! Đó là hai vụ xử án tiêu biểu của Tiến sĩ Phạm Công Trứ và Cử nhân Vũ Văn Tuấn.;", 93, 9);
insert into BOOK values (311, "Thần Đồng Đất Việt 32 - Nỗi Oan Xã Bạc", 10000.0, 8500.0, false, false, false, false, true, current_timestamp(), 88, "Thần Đồng Đất Việt 32 - Nỗi Oan Xã Bạc Năm lần bảy lượt phá hoại ruộng dưa của Tám Tiền, đổ oan cho người vô tội, ai mà to gan thế? Nhưng… đụng đến thần đồng, liệu có yên thân? Hãy xem tài xử án của Án Sát Nguyễn Khoa Đăng!;", "Thần Đồng Đất Việt 32 - Nỗi Oan Xã Bạc Năm lần bảy lượt phá hoại ruộng dưa của Tám Tiền, đổ oan cho người vô tội, ai mà to gan thế? Nhưng… đụng đến thần đồng, liệu có yên thân? Hãy xem tài xử án của Án Sát Nguyễn Khoa Đăng!;", 138, 9);
insert into BOOK values (312, "Hotgirl Tắc Kè - Tập 2 : Chuyên Gia Ra Chưởng", 35000.0, 29750.0, false, false, false, false, true, current_timestamp(), 88, "Kết thúc tập 1, sau bao nhiêu nỗ lực sống ảo, hot girl triệu like nổi tiếng số một vũ trụ trên mạng xã hội Mina đã hiện nguyên hình là cô nàng mặt mụn học dốt Tưởng Mỹ Nhân trước anh bán chè Phù Khắc Nổi!;Kinh khủng hơn, vì lỡ lấy nhầm “bảo bối” và gây hấn với Nổi, Tưởng Mỹ Nhân phải đứng trước sự lựa chọn kì quặc: Chấp nhận một tên bán chè lề đường trở thành chuyên gia chăm sóc sắc đẹp hoặc danh tính thực sự của hot girl Mina có nguy cơ bị bại lộ trước bàn dân thiên hạ!;", "Kết thúc tập 1, sau bao nhiêu nỗ lực sống ảo, hot girl triệu like nổi tiếng số một vũ trụ trên mạng xã hội Mina đã hiện nguyên hình là cô nàng mặt mụn học dốt Tưởng Mỹ Nhân trước anh bán chè Phù Khắc Nổi!;Kinh khủng hơn, vì lỡ lấy nhầm “bảo bối” và gây hấn với Nổi, Tưởng Mỹ Nhân phải đứng trước sự lựa chọn kì quặc: Chấp nhận một tên bán chè lề đường trở thành chuyên gia chăm sóc sắc đẹp hoặc danh tính thực sự của hot girl Mina có nguy cơ bị bại lộ trước bàn dân thiên hạ!;Nhượng bộ Nổi, hay tượng đài hot girl trên thế giới ảo sụp đổ?;Phù Khắc Nổi rốt cục là ai? Động cơ thực sự của hắn tiếp cận Mỹ Nhân là gì?;Cuộc sống của Tưởng Mỹ Nhân sẽ như thế nào khi phải phân thân sống ở hai thế giới thực và ảo?;Tập 2 Hot Girl Tắc Kè – CHUYÊN GIA RA CHƯỞNGsẽ giải đáp tất cả!;", 44, 9);
